import React, { useState } from 'react';
import "./Text.css";

function Text() {
  const [characterArea, styleArea, plotArea, setCharactersArea, setStyleArea, setPlotArea] = useState('');
  const [answer, setAnswer] = useState('');
  const [promptResponse, setPromptResponse] = useState('');

  const handleSubmit = async () => {
    const url = 'http://localhost:4444/api/prompt';
    var tmpPromptResponse = '';
    try {
      const response = await fetch(url , {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          characters: characterArea,
          style: styleArea,
          plot: plotArea
        }),
      });

      // eslint-disable-next-line no-undef
      let decoder = new TextDecoderStream();
      if (!response.body) return;
      const reader = response.body
        .pipeThrough(decoder)
        .getReader();

      while (true) {
        var {value, done} = await reader.read();

        if (done) {
          break;
        } else {
          tmpPromptResponse += value;
          setPromptResponse(tmpPromptResponse);
        }
      }
    } catch (error) {
      console.log(error);
    }

  }
  return (
    <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'center'}}>

      <div style={{order: 1, width: '80vh'}}>
        <div style={{display: 'flex', flexDirection: 'column'}}>
          <h2 style={{order: -1}}>Try with your keyboard!</h2> 
            <h3>Introduce your main characters!</h3>       
            <textarea                      
              rows={10}
              onChange={(e) => setCharactersArea(e.target.value)}
              style={{order: 2, marginBottom: '1rem'}}
              value={characterArea}
              ></textarea>


            <h3>Introduce your style</h3>       
            <textarea                      
              rows={10}
              onChange={(e) => setStyleArea(e.target.value)}
              style={{order: 2, marginBottom: '1rem'}}
              value={styleArea}
              ></textarea>

            <h3>Introduce </h3>       
            <textarea                      
              rows={10}
              onChange={(e) => setPlotArea(e.target.value)}
              style={{order: 2, marginBottom: '1rem'}}
              value={plotArea}
              ></textarea>


          <div style={{order: 3}}>
            <button
              onClick={handleSubmit}
            >Submit</button>
          </div>

          <div style={{order: 4, marginTop: '1rem'}}>
            <h3>Streamed Prompt Response:</h3>
            <span>{promptResponse}</span>
          </div>

        </div>      
      </div>
    </div>
  );
}

export default Text;
