import Webcam from "react-webcam";

import { useCallback, useRef, useState } from "react"; // import useCallback

const CustomWebcam = () => {
  const webcamRef = useRef(null);
  const [imgSrc, setImgSrc] = useState(null);
  const [mirrored, setMirrored] = useState(false);
  const fs = require('fs')
  // create a capture function
  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImgSrc(imageSrc);
  }, [webcamRef]);


  takePicture = () => {
    this.camera.capture()
      .then(blob => {
        console.log(blob);
        const image = fs.readFileSync(blob.path);
        const b64Image = Buffer.from(image).toString('base64');
        this.props.dispatch(uploadImage(b64image));
      })
  }


  const retake = () => {
    setImgSrc(null);
  };

    return (
        <div className="container">
        {imgSrc ? (
          <img src={imgSrc} alt="webcam" />
        ) : (
          <Webcam height={600} width={600} ref={webcamRef} mirrored={mirrored} />
        )}
        
      <div className="btn-container">
        {imgSrc ? (
          <button onClick={retake}>Retake photo</button>
        ) : (
          <button onClick={capture}>Capture photo</button>
        )}
      </div>
    </div>
    );
};

export default CustomWebcam;
