// export default Home;



import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './html/style.css';
import Home from './Home';
import SelectInput from './SelectInput';
import Text from './Text';
import Voice from './Voice'; 
import Makaton from './Makaton'; 


import React, { useEffect } from 'react';

function App() {
  useEffect(() => {
    const handleScroll = () => {
      const stars = document.getElementById('./html/stars');
      const moon = document.getElementById('./html/moon');
      const mountainsBehind = document.getElementById('./html/mountains_behind');
      const text = document.getElementById('./html/text');
      const btn = document.getElementById('./html/btn');
      const mountainsFront = document.getElementById('./html/mountains_front');
      const header = document.querySelector('header');

      const value = window.scrollY;
      stars.style.left = value * 0.25 + 'px';
      moon.style.top = value * 1.05 + 'px';
      mountainsBehind.style.top = value * 0.5 + 'px';
      mountainsFront.style.top = value * 0 + 'px';
      text.style.marginRight = value * 4 + 'px';
      text.style.marginTop = value * 1.5 + 'px';
      btn.style.marginTop = value * 1.5 + 'px';
      header.style.top = value * 0.5 + 'px';
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div>
      <header>
        <div className="logo-wrapper">
          <a href="#" className="logo">
            MAKASTORY
          </a>
          <a href="#" className="subtitle">
            Generating stories for kids
          </a>
        </div>
        <ul>
        <Link to="/voice">
          <button className="large-button">
            Voice
          </button>
        </Link>
        <Link to="/text">
          <button className="large-button">
            Text
          </button>
        </Link>
        <Link to="/makaton">
        <button className="large-button">
          Sign
        </button>
        </Link>
        </ul>
      </header>
      <section>
        <img src="./html/stars.png" id="stars" alt="Stars" />
        <img src="./html/moon.png" id="moon" alt="Moon" />
        <img src="./html/mountains_behind.png" id="mountains_behind" alt="Mountains Behind" />
        <h2 id="text">Start Adventure</h2>
          Explore
        <img src="./html/mountains_front.png" id="mountains_front" alt="Mountains Front" />
      </section>
    </div>
  );
}

export default App;
